import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

const Profile = () => {
  let id = localStorage.getItem("id");
  console.log(id);
  const [data, setData] = useState({});

  const [all, setall] = useState("");
  const [done, setdone] = useState(false);
  const [done2, setdone2] = useState(false);
  const [done3, setdone3] = useState(false);
  const [done4, setdone4] = useState(false);
  const [done5, setdone5] = useState(false);


  const passPattern = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{7,15}$/;
  const namePattern = /(^[a-zA-Z][a-zA-Z\s]{2,20}[a-zA-Z]$)/;
  const emailPattern =/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  const phonePattern = /[07]{2,3}[7-9]{1,2}[0-9]{7,8}/;


  const fetchProfile = async () => {
    const response = await fetch(`http://127.0.0.1:8000/api/get/${id}`);
    const myProfile = await response.json();

    setData(myProfile);
    console.log("**********************");
    console.log(myProfile);
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  function handelSubmet(e) {
    e.preventDefault();

    axios
      .post(`http://127.0.0.1:8000/api/update/` + id, { ...data })
      .then((res) => {
        console.log(res.data);
        if (res.status == 200) {
          Swal.fire({
            title: "Profile",
            text: "Profile Updated Successfully",
            type: "success",
            confirmButtonColor: "#ea512e",
          });
        }
        localStorage.setItem("id", res.data.id);
      });
  }

  //  const handleChangeImage = (e) => {
  //    setData({
  //      ...data,
  //      image: e.target.files[0],
  //    });
  //  };

  return (
    <>
      <div class="page-banner-area item-bg3">
        <div class="d-table">
          <div class="d-table-cell">
            <div class="container">
              <div class="page-banner-content">
                {/* <h2>How to Apply</h2>
                            <ul>
                                <li>
                                    <a href="index.html">Home</a>
                                </li>
                                <li>How to Apply</li>
                            </ul> */}
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* <!-- End Page Banner -->

        <!-- Start Apply Area --> */}
      <section class="apply-area ptb-100">
        <div class="container">
          <div class="row">
            <div class="col-lg-6">
              <div class="quote-image">
                <img
                  src={"http://localhost:8000/upload/" + data.image}
                  style={{ width: "100%", height: "100%" }}
                  alt="image"
                />
              </div>
            </div>

            <div class="col-lg-6">
              <div class="quote-item">
                <div class="content">
                  <span>You are welcome</span>
                  {/* <h3>Online Class Registration</h3> */}
                </div>

                <form onSubmit={handelSubmet}>
                  <div class="form-group">
                    <label>Name</label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Full Name"
                      defaultValue={data.name}
                      onChange={(e) =>
                        setData((prev) => ({ ...prev, name: e.target.value }))
                      }
                    />
                  </div>
                  <br />

                  <div class="form-group">
                    <label>Email</label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Email"
                      defaultValue={data.email}
                      onChange={(e) =>
                        setData((prev) => ({ ...prev, email: e.target.value }))
                      }
                    />
                  </div>
                  <br />

                  <div class="form-group">
                    <label>Phone Number</label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Phone Number"
                      defaultValue={data.mobile}
                      onChange={(e) =>
                        setData((prev) => ({ ...prev, mobile: e.target.value }))
                      }
                    />
                  </div>
                  <br />

                  <div class="form-group">
                    <label>Password</label>
                    <input
                      type="password"
                      class="form-control"
                      placeholder="password"
                      //   defaultValue={data.password}
                      onChange={(e) =>
                        setData((prev) => ({
                          ...prev,
                          password: e.target.value,
                        }))
                      }
                    />
                  </div>
                  <br />
                  <label>
                    Change your profile picture : &nbsp;&nbsp;&nbsp;
                  </label>
                  <input
                    type="file"
                    // onChange={}
                    name="image"
                  />
                  <br></br>
                  <br></br>
                  <button type="submit" class="default-btn">
                    UPDATE
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Profile;
