import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import Swal from "sweetalert2";

export const getComunityComments = createAsyncThunk(
  "comunitycomment/getComunityComments",
  async () => {
    const api = await fetch("http://127.0.0.1:8000/api/apiCommunityComment");
    const response = await api.json();
    console.log(response);
    return response;
  }
);

const comSlice = createSlice({
  name: "comunitycomment",
  initialState: { status: null, comunitycomments: [] },
  extraReducers: {
    //get comunitypost from api
    [getComunityComments.fulfilled]: (state, action) => {
        console.log(action);
      state.status = "success fetch data";
      state.comunitycomments = action.payload;
    },
    [getComunityComments.pending]: (state) => {
      state.status = "pending  fetch data";
    },
    [getComunityComments.rejected]: (state) => {
      state.status = "rejected  fetch data";
    },

  },
});

export default comSlice.reducer;
