import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const getCommunityComments = createAsyncThunk(
  "commentcomunity/getCommunityComments",
  async () => {
    const api = await fetch("http://127.0.0.1:8000/api/apiCommunityComment");
    const response = await api.json();
    console.log(response);
    return response;
  }
);



const postSlice = createSlice({
  name: "commentcomunity",
  initialState: { status: null, ommentcomunitys: [] },
  extraReducers: {
    //get item from api
    [getCommunityComments.fulfilled]: (state, action) => {
      state.status = "success fetch data";
      state.posts = action.payload;
    },
    [getCommunityComments.pending]: (state) => {
      state.status = "pending  fetch data";
    },
    [getCommunityComments.rejected]: (state) => {
      state.status = "rejected  fetch data";
    },
  },
});

export default postSlice.reducer;
