import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { signup } from "../../redux/userSlice";
import { addUser } from "../../redux/userLoginSlice";
import axios from 'axios'

const Sign_up = () => {
  const [userData, setUser] = useState({ name: "", email: "", password: "" });
  let navigate = useNavigate();
  const user = useSelector((state) => state.user.isLogged);
  const dispatch = useDispatch();

console.log(user)

  async function handelSubmet(e) {
    e.preventDefault();

    // console.log(user);
    // const name = 1;
    // dispatch(signup(name));
    // console.log(userData);

    // const formData = new FormData();
    // formData.append('name', userData.name)
    // formData.append('email', userData.email)
    // formData.append('password', userData.password)
    // dispatch(addUser(formData));
    
    console.log(userData);



    axios.post(`http://127.0.0.1:8000/api/register`, { ...userData })
    .then(res => {
      console.log(user);
      console.log(res.data);
      dispatch(signup(res.data.name));
      localStorage.setItem('id',res.data.id )
      

    })

  }

  useEffect(() => {
    if (user) {
      navigate("/", { replace: true });
    } else {
      navigate("/Sign_Up", { replace: true });
    }
  }, [user]);

  return (
    <>
      <section class="login-area ptb-100">
        <div class="container">
          <div class="login-form">
            <h2>Sign Up</h2>

            <form onSubmit={handelSubmet}>
              <div class="form-group">
                {/* <label>Username</label> */}
                <input
                  type="text"
                  class="form-control"
                  placeholder="Username"
                  // value={name}
                  // onChange={(e) => setname(e.target.value)}/>

                  onChange={(e) =>
                    setUser((prev) => ({ ...prev, name: e.target.value }))
                  }
                />
              </div>

              <div class="form-group">
                {/* <label>Email or phone</label> */}
                <input
                  type="email"
                  class="form-control"
                  placeholder="Email"
                  // value={email}
                  //  onChange={(e) => setemail(e.target.value)}/>

                  onChange={(e) =>
                    setUser((prev) => ({ ...prev, email: e.target.value }))
                  }
                />
              </div>

              <div class="form-group">
                {/* <label>Password</label> */}
                <input
                  type="password"
                  class="form-control"
                  placeholder="Password"
                  //   value={password}
                  //   onChange={(e) => setpassword(e.target.value)}/>

                  onChange={(e) =>
                    setUser((prev) => ({ ...prev, password: e.target.value }))
                  }
                />
              </div>

              <div class="form-group">
                {/* <label>Password</label> */}
                <input
                  type="password"
                  class="form-control"
                  placeholder="Confirm Password"
                  //   value={confirm_password}
                  //   onChange={(e) => setconfirm_password(e.target.value)}
                />
              </div>

              <div class="row align-items-center">
                <div class="col-lg-6 col-md-6 col-sm-6">
                  <div class="form-check">
                    <input
                      type="checkbox"
                      class="form-check-input"
                      id="checkme"
                    />
                    <label class="form-check-label" for="checkme">
                      Remember me
                    </label>
                  </div>
                </div>

                <div class="col-lg-6 col-md-6 col-sm-6 lost-your-password">
                  <a href="#" class="lost-your-password">
                    Forgot your password?
                  </a>
                </div>
              </div>

              <button type="submit">Sign Up</button>
            </form>

            <div class="important-text">
              <p>
                Already have account?{" "}
                <Link to="/Login">
                  <a href="register.html">Login now!</a>
                </Link>
              </p>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Sign_up;
