    class Sign_up extends React.Component {
      constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: [],
        };
      }

      componentDidMount() {
        fetch("http://127.0.0.1:8000/api/users")
          .then((res) => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result,
              });
            },

            (error) => {
              this.setState({
                isLoaded: true,
                error,
              });
            }
          );
      }

      render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <ul>
              {items.map((item) => (
                <li key={item.id}>
                  {item.name}
                 {item.price}
                </li>
              ))}
            </ul>
          );
        }
      }
    }
