import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

const Profile = () => {
  let id = localStorage.getItem("id");
  console.log(id);
  const [data, setData] = useState({});


  const [name, setname] = useState("");
  const [email, setemail] = useState("");
  const [pass, setpass] = useState("");
  const [num, setnum] = useState("");
  const [img, setimg] = useState("");





  const [all, setall] = useState("");
  const [done, setdone] = useState(false);
  const [done2, setdone2] = useState(false);
  const [done3, setdone3] = useState(false);
  const [done4, setdone4] = useState(false);
  const [done5, setdone5] = useState(false);


  const passPattern = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{7,15}$/;
  const namePattern = /(^[a-zA-Z][a-zA-Z\s]{2,20}[a-zA-Z]$)/;
  const emailPattern =/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  const phonePattern = /[07]{2,3}[7-9]{1,2}[0-9]{7,8}/;
  // const regex  = "([^\\s]+(\\.(?i)(jpe?g|png|gif|bmp))$)";
  const imgpattern= /^(.+\/)+.+(\.(gif|png|jpg|jpeg|webp|svg|psd|bmp|tif|jfif))$/i;
  const sizepattern = /^\d+X\d+$/;


  const fetchProfile = async () => {
    const response = await fetch(`http://127.0.0.1:8000/api/get/${id}`);
    const myProfile = await response.json();

    if(data.image == "")
    {
      data.image = "http://localhost:8000/upload/" + data.image;
    }
    setData(myProfile);
    console.log("**********************");
    console.log(myProfile);
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  function handelSubmet(e) {
    e.preventDefault();

    setname("");
    setemail("");
    setpass("");
    setnum("");
    setimg("");

  
    setall("");
    setdone(false);
    setdone2(false);
    setdone3(false);
    setdone4(false);
    setdone5(false);



    if (data.name != "" && data.email != ""  ) {
      if (namePattern.test(data.name)) {
        setdone(true);
      } else {
        setdone(false);
        setname(
          <h6 style={{ color: "red", textAlign: "center" }}>
            * just use liters (a-z)&&(A-Z) <br></br>*name must be at least 3
            liters{" "}
          </h6>
        );
      }

      if (emailPattern.test(data.email)) {
        setdone2(true);
      } else {
        setdone2(false);
        setemail(
          <h6 style={{ color: "red", textAlign: "center" }}>
            * Enter correct email
          </h6>
        );
      }

      if (passPattern.test(data.password)) {
        setdone3(true);
      } else {
        setdone3(false);
        setpass(
          <h6 style={{ color: "red", textAlign: "center" }}>
            - Minimum 6 characters
            <br></br>- At least 1 upper case English letter <br></br> - At least
            1 letter<br></br>- At least 1 special character{" "}
          </h6>
        );
      }

      if (pass == conferm2) {
        setdone4(true);
      } else {
        setdone4(false);
        setconferm2(
          <h6 style={{ color: "red", textAlign: "center" }}>
            conferm password mush match password
          </h6>
        );
      }

      if (done && done2 && done3 && done4) {
        axios
          .post(`http://127.0.0.1:8000/api/register`, { ...userData })
          .then((res) => {
            console.log(user);
            console.log(res.data);
            dispatch(signup(res.data.name));
            localStorage.setItem("id", res.data.id);
          });
      }
    } else {
      setall(
        <h5 style={{ color: "red", textAlign: "center" }}>
          
          ***  Name and Email are required  ***
        </h5>
      );
    }
  

































    axios
      .post(`http://127.0.0.1:8000/api/update/` + id, { ...data })
      .then((res) => {
        console.log(res.data);
        if (res.status == 200) {
          Swal.fire({
            title: "Profile",
            text: "Profile Updated Successfully",
            type: "success",
            confirmButtonColor: "#ea512e",
          });
        }
        localStorage.setItem("id", res.data.id);
      });
  }

  //  const handleChangeImage = (e) => {
  //    setData({
  //      ...data,
  //      image: e.target.files[0],
  //    });
  //  };































  return (
    <>
      <div class="page-banner-area item-bg3">
        <div class="d-table">
          <div class="d-table-cell">
            <div class="container">
              <div class="page-banner-content">
                {/* <h2>How to Apply</h2>
                            <ul>
                                <li>
                                    <a href="index.html">Home</a>
                                </li>
                                <li>How to Apply</li>
                            </ul> */}
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* <!-- End Page Banner -->

        <!-- Start Apply Area --> */}
      <section class="apply-area ptb-100">
        <div class="container">
          <div class="row">
            <div class="col-lg-6">
              <div class="quote-image">
                <img
                  src={data.image}
                  style={{ width: "100%", height: "100%" }}
                  alt="image"
                />
              </div>
            </div>

            <div class="col-lg-6">
              <div class="quote-item">
                <div class="content">
                  <span>You are welcome</span>
                  {/* <h3>Online Class Registration</h3> */}
                </div>
{/* /////////////////////////////////////////////////////////////////////////////////////////////////////////// */}
                <form onSubmit={handelSubmet}>
                  <div class="form-group">
                    <label>Name</label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Full Name"
                      defaultValue={data.name}
                      onChange={(e) =>
                        setData((prev) => ({ ...prev, name: e.target.value }))
                      }
                    />
                  </div>
                  <br />

                  <div class="form-group">
                    <label>Email</label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Email"
                      defaultValue={data.email}
                      onChange={(e) =>
                        setData((prev) => ({ ...prev, email: e.target.value }))
                      }
                    />
                  </div>
                  <br />

                  <div class="form-group">
                    <label>Phone Number</label>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Phone Number"
                      defaultValue={data.mobile}
                      onChange={(e) =>
                        setData((prev) => ({ ...prev, mobile: e.target.value }))
                      }
                    />
                  </div>
                  <br />

                  <div class="form-group">
                    <label>Password</label>
                    <input
                      type="password"
                      class="form-control"
                      placeholder="password"
                      //   defaultValue={data.password}
                      onChange={(e) =>
                        setData((prev) => ({
                          ...prev,
                          password: e.target.value,
                        }))
                      }
                    />
                  </div>
                  <br />
                  <label>
                    Change your profile picture : &nbsp;&nbsp;&nbsp;
                  </label>
                  <input
                    type="file"
                    // onChange={}
                    name="image"
                  />
                  <br></br>
                  <br></br>
                  <button type="submit" class="default-btn">
                    UPDATE
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Profile;
