import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getSingleComunity,
  getComunityPosts,
  deleteComunityPost,
  addComunityPosts,
  updateComunityPosts,
  getComcomments,
  addComunityComment,
} from "../redux/ComunityPost";
import { useParams } from "react-router-dom";
import { Icon } from "@iconify/react";

const SingleComunity = () => {
  const dispatch = useDispatch();

    const { id } = useParams();
  // console.log(id)
  useEffect(() => {
    dispatch(getComunityPosts());
  }, [dispatch]);
  const comunityposts = useSelector((state) => state.comunitypost);
  //  console.log(comunityposts);

  const [comunityData, setComunityData] = useState({
    comment_comunity_posts: " ",
    subject: " ",
    user_id_ComunityPost: localStorage.id,
  });
  // console.log(id)
  const handleChange = (e) => {
    e.preventDefault();

    const value = e.target.value;
    setComunityData({
      ...comunityData,
      [e.target.name]: value,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    dispatch(addComunityPosts(comunityData));
  };

  // const [showEdit, setShowEdit] = useState(false);

  // const isEdit = (e) => {
  //   setShowEdit(true);
  // };
  // //////
  // // const commentId= comment1.comments.id;
  // // console.log(commentId)
  // const handleSubmitEdit = (e) => {
  //   e.preventDefault();
  //   setShowEdit(false);

  //   dispatch(updateComunityPosts, comunityData);

  //   console.log(comunityData);
  // };

  const [comunitycommentData, setComunitycommentData] = useState({
    comment_comunity_comments: " ",
    user_id_ComunityComment: localStorage.id,
    post_id_ComunityComment: "",
  });

  const handleChangeCom = (e) => {
    e.preventDefault();

    const value = e.target.value;
    setComunitycommentData({
      ...comunitycommentData,
      [e.target.name]: value,
    });
  };
  const handleSubmitCom = (e) => {
    e.preventDefault();
  };
  console.log(comunitycommentData);

  useEffect(() => {
    dispatch(getComcomments());
  }, [dispatch]);
  // const comunitycomments = useSelector((state) => state.comcomments);

  return (
    <>
      <div class="page-banner-area item-bg3">
        <div class="d-table">
          <div class="d-table-cell">
            <div class="container">
              <div class="page-banner-content">
                <h2>Community</h2>
                <ul>
                  <li>
                    <a href="index.html">Home</a>
                  </li>
                  <li>Blog Details</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* <!-- End Page Banner -->

        <!-- Start Blog Details Area --> */}

      <section class="blog-details-area ptb-100">
        <section class="class-area bg-fdf6ed pt-100 pb-70">
          <div class="container">
            <div class="section-title">
              <span>Classes</span>
              <h2>Popular Classes</h2>
            </div>

            <div class="row">
              {comunityposts.comunityposts.map((post) => {
                return (
                  <div class="col-lg-4 col-md-6" key={post.id}>
                    <div class="single-class">
                      <div class="class-image">
                        <a href="#">
                          <img src="assets/img/class/class-1.jpg" alt="image" />
                        </a>
                      </div>

                      <div class="class-content">
                        <div class="price">
                          {" "}
                          <button
                            style={{
                              background: "none",
                              color: "inherit",
                              border: "none",
                              padding: " 10px",
                              font: "inherit",
                              outline: "inherit",
                            }}
                            onClick={() => {
                              dispatch(deleteComunityPost(post.id));
                            }}
                          >
                            <a class="comment-reply-link">
                              <Icon
                                icon="material-symbols:cancel-rounded"
                                style={{ fontSize: "24px", color: "white" }}
                              />
                            </a>
                          </button>
                          {/* <button
                            onClick={() => {
                              setComunityData(comunityposts);
                              isEdit();
                            }}
                            style={{
                              background: "none",
                              color: "inherit",
                              border: "none",
                              padding: 0,
                              font: "inherit",
                              outline: "inherit",
                            }}
                          >
                            <a class="comment-reply-link">
                              <Icon
                                icon="fa6-regular:pen-to-square"
                                style={{ fontSize: "24px", color: "white" }}
                              />
                            </a>
                          </button> */}
                        </div>

                        <h3>
                          <a href="#">{post.subject}</a>
                        </h3>
                        <p>{post.comment_comunity_posts}</p>

                        <ul class="class-list">
                          <div
                            style={{
                              color: "#ff6100",
                              display: "flex",
                              justifyContent: "end",
                              padding: "2%",
                            }}
                          >
                            <button
                              style={{
                                background: "none",
                                color: "inherit",
                                border: "none",
                                padding: " 10px",
                                font: "inherit",
                                outline: "inherit",
                              }}
                            >
                              <span>View more comments</span>
                              <Icon
                                icon="emojione-monotone:red-triangle-pointed-down"
                                style={{
                                  fontSize: "14px",
                                  marginTop: "1.5%",
                                }}
                              />
                            </button>
                          </div>
                        </ul>
                        <button
                          style={{
                            background: "none",
                            color: "inherit",
                            border: "none",
                            padding: " 10px",
                            font: "inherit",
                            outline: "inherit",
                          }}
                        >
                          <div class="class-btn">
                            <a href="#" class="default-btn">
                              Repley
                            </a>
                          </div>
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
          <div class="class-shape">
            <div class="shape-1">
              <img src="assets/img/class/class-shape-1.png" alt="image" />
            </div>
            <div class="shape-2">
              <img src="assets/img/class/class-shape-2.png" alt="image" />
            </div>
          </div>
        </section>
      </section>
    </>
  );
};
export default SingleComunity;
