import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getComunityPosts,
  deleteComunityPost,
  addComunityPosts,
  updateComunityPosts,
} from "../redux/ComunityPost";
import { useParams } from "react-router-dom";
import { Icon } from "@iconify/react";

const Comunity = () => {
  const dispatch = useDispatch();

  const { id } = useParams();

  useEffect(() => {
    dispatch(getComunityPosts());
  }, [dispatch]);
  const comunityposts = useSelector((state) => state.comunitypost);
  //  console.log(comunityposts);

  const [comunityData, setComunityData] = useState({
    comment_comunity_posts: " ",
    subject:" ",
    user_id_ComunityPost: localStorage.id,
  });
  // console.log(id)
  const handleChange = (e) => {
    e.preventDefault();

    const value = e.target.value;
    setComunityData({
      ...comunityData,
      [e.target.name]: value,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    dispatch(addComunityPosts(comunityData));
  };

  // const [showEdit, setShowEdit] = useState(false);

  // const isEdit = (e) => {
  //   setShowEdit(true);
  // };
  // //////
  // // const commentId= comment1.comments.id;
  // // console.log(commentId)
  // const handleSubmitEdit = (e) => {
  //   e.preventDefault();
  //   setShowEdit(false);

  //   dispatch(updateComunityPosts, comunityData);

  //   console.log(comunityData);
  // };

  return (
    <>
      <div class="page-banner-area item-bg3">
        <div class="d-table">
          <div class="d-table-cell">
            <div class="container">
              <div class="page-banner-content">
                <h2>Community</h2>
                <ul>
                  <li>
                    <a href="index.html">Home</a>
                  </li>
                  <li>Blog Details</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* <!-- End Page Banner -->

        <!-- Start Blog Details Area --> */}

      <section class="blog-details-area ptb-100">
        <div class="container">
          <div class="comments-area">
            <div class="comment-respond">
              <h3 class="comment-reply-title">Leave a Reply</h3>
              {/* comment start */}
              <form class="comment-form" onSubmit={handleSubmit}>
                <p class="comment-notes">
                  <span id="email-notes">
                    Your email address will not be published.
                  </span>
                  Required fields are marked
                  <span class="required">*</span>
                </p>
                <h5>Subject</h5>
                <input type="text" onChange={handleChange} name="subject" required/>

                <h5>Post</h5>
                <p class="comment-form-comment">
                  <label>Comment</label>

                  <textarea
                    onChange={handleChange}
                    name="comment_comunity_posts"
                    cols="45"
                    placeholder="Your Comment..."
                    rows="5"
                    maxlength="65525"
                    required="required"
                  ></textarea>
                </p>
                <p class="comment-form-cookies-consent">
                  <input
                    type="checkbox"
                    value="yes"
                    name="wp-comment-cookies-consent"
                    id="wp-comment-cookies-consent"
                  />
                  <label for="wp-comment-cookies-consent">
                    Save my name, email, and website in this browser for the
                    next time I comment.
                  </label>
                </p>
                <p class="form-submit">
                  <input
                    type="submit"
                    name="submit"
                    id="submit"
                    class="submit"
                    value="Post A Comment"
                  />
                </p>
              </form>
            </div>
          </div>
        </div>
        <section class="class-area bg-fdf6ed pt-100 pb-70">
          <div class="container">
            <div class="section-title">
              <span>Classes</span>
              <h2>Popular Classes</h2>
            </div>

            <div class="row">
              {comunityposts.comunityposts.map((post) => {
                return (
                  <div class="col-lg-4 col-md-6" key={post.id}>
                    <div class="single-class">
                      <div class="class-image">
                        <a href="#">
                          <img src="assets/img/class/class-1.jpg" alt="image" />
                        </a>
                      </div>

                      <div class="class-content">
                        <div class="price">
                          {" "}
                          <button
                            style={{
                              background: "none",
                              color: "inherit",
                              border: "none",
                              padding: " 10px",
                              font: "inherit",
                              outline: "inherit",
                            }}
                            onClick={() => {
                              dispatch(deleteComunityPost(post.id));
                            }}
                          >
                            <a class="comment-reply-link">
                              <Icon
                                icon="material-symbols:cancel-rounded"
                                style={{ fontSize: "24px", color: "white" }}
                              />
                            </a>
                          </button>
                          {/* <button
                            onClick={() => {
                              setComunityData(comunityposts);
                              isEdit();
                            }}
                            style={{
                              background: "none",
                              color: "inherit",
                              border: "none",
                              padding: 0,
                              font: "inherit",
                              outline: "inherit",
                            }}
                          >
                            <a class="comment-reply-link">
                              <Icon
                                icon="fa6-regular:pen-to-square"
                                style={{ fontSize: "24px", color: "white" }}
                              />
                            </a>
                          </button> */}
                        </div>
                        <h3>
                          <a href="#">{post.subject}</a>
                        </h3>
                        <p>{post.comment_comunity_posts}</p>
                        
                        <ul class="class-list">
                          <input
                            type="text"
                            placeholder="Write a comment"
                            style={{
                              fontSize: "24px",
                              borderColor: "#ea512e94",
                            }}
                          />
                        </ul>

                        <div class="class-btn">
                          <a href="#" class="default-btn">
                            Reply
                          </a>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
          {/* {showEdit && (
            <form onSubmit={handleSubmitEdit}>
              <div className="form-group">
                <p class="comment-form-comment">
                  <textarea
                    onChange={handleChange}
                    name="comment_comunity_posts"
                    cols="45"
                    placeholder="Your Comment..."
                    rows="5"
                    value={comunityData.comment_comunity_posts}
                    maxlength="65525"
                  ></textarea>
                </p>
              </div>
              <div className="form-group" style={{ width: "10%" }}>
                <button className="form-control  btn btn-primary" type="submit">
                  update
                </button>
              </div>
            </form>
          )} */}
          <div class="class-shape">
            <div class="shape-1">
              <img src="assets/img/class/class-shape-1.png" alt="image" />
            </div>
            <div class="shape-2">
              <img src="assets/img/class/class-shape-2.png" alt="image" />
            </div>
          </div>
        </section>
      </section>
    </>
  );
};
export default Comunity;
