import { useEffect, useState } from "react";
import { Icon } from "@iconify/react";
import { useDispatch, useSelector } from "react-redux";
import { getPosts, getSinglePosts } from "../redux/postSlice";
import { useParams } from "react-router-dom";
import {
  addComment,
  getComments,
  deleteComment,
  updateComment,
} from "../redux/commentSlice";
import Swal from "sweetalert2";
const Comunity = () => {
  const { id } = useParams();

  // ///posts///
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  const posts = useSelector((state) => state.post);
  // console.log(posts);
  //////single post///////////

  useEffect(() => {
    dispatch(getSinglePosts(id));
  }, [dispatch]);
  const singlepost = useSelector((state) => state.post);

  // console.log(singlepost);
  ////// end single post ///////

  // //////////Comment //////////
  useEffect(() => {
    dispatch(getComments(id));
  }, [dispatch]);
  const comment1 = useSelector((state) => state.comment);
  /////
  let currentTimestamp = Date.now();
  let date = new Intl.DateTimeFormat("en-US", {
    year: "numeric",
    month: "2-digit",
    day: "2-digit",
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
  }).format(currentTimestamp);
  console.log(date);

  ////

  const [commentData, setCommentData] = useState({
    date: date,
    comment: " ",
    user_id_comment: localStorage.id,
    post_id_comment: id,
  });
  // console.log(id)
  const handleChange = (e) => {
    e.preventDefault();

    const value = e.target.value;
    setCommentData({
      ...commentData,
      [e.target.name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    dispatch(addComment(commentData));
  };
  /////////start edit////////
  const [showEdit, setShowEdit] = useState(false);

  const isEdit = (e) => {
    setShowEdit(true);
  };
  //////
  // const commentId= comment1.comments.id;
  // console.log(commentId)
  const handleSubmitEdit = (e) => {
    e.preventDefault();
    setShowEdit(false);

    dispatch(updateComment(commentData));

    console.log(commentData);
  };

  /////end edit/////

  return (
    <>
      <div class="page-banner-area item-bg3">
        <div class="d-table">
          <div class="d-table-cell">
            <div class="container">
              <div class="page-banner-content">
                <h2>Comunity</h2>
                <ul>
                  <li>
                    <a href="index.html">Home</a>
                  </li>
                  <li>Blog Details</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* <!-- End Page Banner -->

        <!-- Start Blog Details Area --> */}

      <section class="blog-details-area ptb-100">
        <div class="container">
          

                <div class="comments-area">
                  <div class="comment-respond">
                    <h3 class="comment-reply-title">Leave a Reply</h3>
                    {/* comment start */}
                    <form class="comment-form" onSubmit={handleSubmit}>
                      <p class="comment-notes">
                        <span id="email-notes">
                          Your email address will not be published.
                        </span>
                        Required fields are marked
                        <span class="required">*</span>
                      </p>

                      <p class="comment-form-comment">
                        <label>Comment</label>

                        <textarea
                          onChange={handleChange}
                          name="comment"
                          cols="45"
                          placeholder="Your Comment..."
                          rows="5"
                          maxlength="65525"
                          required="required"
                        ></textarea>
                      </p>
                      <p class="comment-form-cookies-consent">
                        <input
                          type="checkbox"
                          value="yes"
                          name="wp-comment-cookies-consent"
                          id="wp-comment-cookies-consent"
                        />
                        <label for="wp-comment-cookies-consent">
                          Save my name, email, and website in this browser for
                          the next time I comment.
                        </label>
                      </p>
                      <p class="form-submit">
                        <input
                          type="submit"
                          name="submit"
                          id="submit"
                          class="submit"
                          value="Post A Comment"
                        />
                      </p>
                    </form>
                  </div>

                  <h3 class="comments-title">
                    <br></br>3 Comments:
                  </h3>
                  {comment1.comments.map((comment) => {
                    return (
                      <ol class="comment-list">
                        <li class="comment">
                          <div class="comment-body">
                            <footer class="comment-meta">
                              <div class="comment-author vcard">
                                <img
                                  src="assets/img/client/client-1.jpg"
                                  class="avatar"
                                  alt="image"
                                />
                                <b class="fn">{comment.name}</b>
                              </div>
                              <div class="comment-metadata">
                                <a href="#">
                                  <span>{comment.date}</span>
                                </a>
                              </div>
                            </footer>
                            <div class="comment-content">
                              <p>{comment.comment}</p>

                              <button
                                onClick={() => {
                                  setCommentData(comment);
                                  isEdit();
                                }}
                                style={{
                                  background: "none",
                                  color: "inherit",
                                  border: "none",
                                  padding: 0,
                                  font: "inherit",
                                  outline: "inherit",
                                }}
                              >
                                <a class="comment-reply-link">
                                  <Icon
                                    icon="fa6-regular:pen-to-square"
                                    style={{ fontSize: "24px" }}
                                  />
                                </a>
                              </button>
                              <button
                                style={{
                                  background: "none",
                                  color: "inherit",
                                  border: "none",
                                  padding: " 10px",
                                  font: "inherit",
                                  outline: "inherit",
                                }}
                                onClick={() => {
                                  dispatch(deleteComment(comment.id));
                                }}
                              >
                                <a class="comment-reply-link">
                                  <Icon
                                    icon="material-symbols:cancel-rounded"
                                    style={{ fontSize: "24px" }}
                                  />
                                </a>
                              </button>
                            </div>
                          </div>
                        </li>
                      </ol>
                    );
                  })}
                </div>
              
            </div>
            {/*edit comment */}
            {showEdit && (
              <form onSubmit={handleSubmitEdit}>
                <div className="form-group">
                  <p class="comment-form-comment">
                    <textarea
                      onChange={handleChange}
                      name="comment"
                      cols="45"
                      placeholder="Your Comment..."
                      rows="5"
                      value={commentData.comment}
                      maxlength="65525"
                    ></textarea>
                  </p>
                </div>
                <div className="form-group" style={{ width: "10%" }}>
                  <button
                    className="form-control  btn btn-primary"
                    type="submit"
                  >
                    update
                  </button>
                </div>
              </form>
            )}

            {/*edit comment end  */}

            <div class="col-lg-4 col-md-12">
              <aside class="widget-area">
                <section class="widget widget_search">
                  <h3 class="widget-title">Search</h3>

                  <form class="search-form">
                    <label>
                      <span class="screen-reader-text">Search for:</span>
                      <input
                        type="search"
                        class="search-field"
                        placeholder="Search..."
                      />
                    </label>
                    <button type="submit">
                      <i class="bx bx-search-alt"></i>
                    </button>
                  </form>
                </section>

                <section class="widget widget_ketan_posts_thumb">
                  <h3 class="widget-title">Popular Posts</h3>
                  {posts.posts.map((post) => {
                    return (
                      <article class="item" key={post.id}>
                        <a href={`/NewsDetails/${post.id}`} class="thumb">
                          <img src="" class="fullimage cover bg1" role="img" />
                        </a>
                        <div class="info">
                          <span>{post.date}</span>
                          <h4 class="title usmall">
                            <a href={`/NewsDetails/${post.id}`}>{post.title}</a>
                          </h4>
                        </div>
                      </article>
                    );
                  })}
                </section>
              </aside>
            </div>
         
      </section>
    </>
  );
};
export default Comunity;
