import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import Swal from "sweetalert2";





export const addComment = createAsyncThunk(
  "comment/addComment",
  async (comment) => {
    const api = await axios.post( 'http://127.0.0.1:8000/api/addComment' ,comment);

    // });
    if (api.status == 200) {
      Swal.fire({
        title: "Comment",
        text: "Has been Added Successfully",
        type: "success",
      });
    }

  }
);


const commentSlice = createSlice({
  name: "comment",
  initialState: { comment: [], status: null },
  extraReducers: {

    //addcomment to api

    [addComment.fulfilled]: (state, action) => {
        console.log(action)
      state.status = "success send data";
      state.comments.push(action.payload);
    },
    [addComment.pending]: (state) => {
      state.status = "pending send data";
    },
    [addComment.rejected]: (state) => {
      state.status = "rejected send data";
    },


  },
});

export default commentSlice.reducer;
