import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";
import Swal from "sweetalert2";
import axios from 'axios'


export const addItem = createAsyncThunk(
    'item/addItem',
    async (item , thunkAPI)=>{

        const response = await axios.post('http://127.0.0.1:8000/api/add_item',item);

        if(response.status === 200){
            // Swal.fire({
            //     title: "Item",
            //     text: "Has been Added Successfully",
            //     type: "success"
            // });
           // alert("item Added Successfully");
        }
        return response.data;
    }
);




const userSlice = createSlice({
    name:'user',
    initialState:{email:"aiman@mail.com" , password:"12345" , isLogged:false},
    reducers:{
        signup:(state , action)=>{
            if(action.payload.email === state.email && action.payload.password === state.password){
                Swal.fire({
                    title: "Login Successfull",
                    text: "Welcome to our site we hope you spent great time with us :)",
                    type: "success"
                })
                state.isLogged = true
            }else{
                Swal.fire({
                    title: "Wrong",
                    text: "incorrect Email or Password",
                    type: "success"
                });

            }
        },
        login:(state , action)=>{
            if(action.payload.email === state.email &&action.payload.password === state.password){
                Swal.fire({
                    title: "Login Successfull",
                    text: "Welcome to our site we hope you spent great time with us :)",
                    type: "success"
                })
                state.isLogged = true
            }else{
                Swal.fire({
                    title: "Wrong",
                    text: "incorrect Email or Password",
                    type: "success"
                });

            }
        },
        logout:(state)=>{
            state.isLogged = false;
        }
    }
});
export const {signup , login , logout} = userSlice.actions;
export default userSlice.reducer;
